import React, { Component } from 'react'
import List from '@material-ui/core/List';
import IconButton from '@material-ui/core/IconButton';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import DeleteIcon from '@material-ui/icons/Delete';
import ListItem from '@material-ui/core/ListItem';
import axios from 'axios';

export class FavoritesList extends Component {
    constructor(props) {
        super(props);

        console.log("in FavoritesList props.user: ", props.user);

    }

    onDeleteFavorite(index) {
       
        console.log("this.props.user.favorites: ", this.props.user.favorites);
        var newFavorites = this.props.user.favorites;
        newFavorites.splice(index,1);

        console.log("Lyrics newFavorites arr ", newFavorites);

        var updatdUser = {
            username: this.props.user.username,
            password: this.props.user.password,
            favorites: newFavorites,
            id: this.props.user._id
        }

        //add to favorites in db
        axios.post('http://localhost:4000/update', updatdUser)
            .then(res => {
                console.log("lyrics update req: ", res.data)
            });
    }


    render() {
        return (
            <div>
                <List >
                    {
                        (this.props.user.favorites) && (
                            this.props.user.favorites.map((item, i) => {
                                console.log("item: ", item)
                                return (
                                    <ListItem key={i}>
                                        <ListItemText key={i}
                                            primary={item.artist + '-' + item.songTitle}
                                        />
                                        <ListItemSecondaryAction>
                                            <IconButton onClick={() => this.onDeleteFavorite(i)} edge="end" aria-label="delete">
                                                <DeleteIcon />
                                            </IconButton>
                                        </ListItemSecondaryAction>
                                    </ListItem>
                                )
                            })
                        )
                    }

                    {/* <ListItem>
                        <ListItemText
                            primary={this.props.user.favorites}
                        />
                        <ListItemSecondaryAction>
                            <IconButton edge="end" aria-label="delete">
                                <DeleteIcon />
                            </IconButton>
                        </ListItemSecondaryAction>
                    </ListItem>, */}

                </List>
            </div>
        )
    }
}

export default FavoritesList
