import { getLyrics, getSong } from 'genius-lyrics-api';
import { Link, animateScroll as scroll } from "react-scroll";
import React, { Component } from 'react'
import StarIcon from '@material-ui/icons/Star';
import FontSizeChanger from 'react-font-size-changer';
import Dotdotdot from 'react-dotdotdot'

import axios from 'axios';

export default class Lyrics extends Component {
    constructor(props) {
        super(props);

        this.onChangeSongTitle = this.onChangeSongTitle.bind(this);
        this.onChangeArtist = this.onChangeArtist.bind(this);
        this.onAddToFavoritesClicked = this.onAddToFavoritesClicked.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        console.log("in lyrics props.lyrics: ", props.lyrics);
        console.log("in lyrics props.song: ", props.song);


        this.state = {
            songTitle: '',
            artist: '',
            lyrics: props.lyrics
        }
    }

    onChangeSongTitle(e) {
        this.setState({
            songTitle: e.target.value
        })
    }

    onChangeArtist(e) {
        this.setState({
            artist: e.target.value
        })
    }

    onAddToFavoritesClicked() {

        console.log("in lyrics onAddToFavoritesClicked ");
        //this.props.onAddToFavoritesClicked(this.state.songTitle, this.state.artist);
        const favoriteSong = {
            songTitle: this.state.songTitle,
            artist: this.state.artist,
        }
        console.log("this.props.user.favorites: ", this.props.user.favorites);
        var newFavorites = this.props.user.favorites;
        newFavorites.push(favoriteSong);

        console.log("Lyrics newFavorites arr ", newFavorites);

        var updatdUser = {
            username: this.props.user.username,
            password: this.props.user.password,
            favorites: newFavorites,
            id: this.props.user._id
        }

        //add to favorites in db
        axios.post('http://localhost:4000/update', updatdUser)
            .then(res => {
                console.log("lyrics update req: ", res.data)
            });
    }

    onSubmit(e) {
        console.log("onSubmit");

        e.preventDefault();

        //this.props.onSearchClicked(this.state.songTitle, this.state.artist);

        const options = {
            apiKey: 'DCCS9ls4wWU8rAylkXUtzBlRrCoA1dIlFF5yINiSp210dT7u-UoZmaOuQYPD-ZPt',
            title: this.state.songTitle,
            artist: this.state.artist,
            optimizeQuery: true
        };

        console.log("options: \n", options);

        getLyrics(options).then((lyrics) => {
            console.log("lyrics: ", lyrics);
            this.setState({ lyrics: lyrics })
        });

    }


    render() {
        return (
            <div>
                <h3>Search For Lyrics</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Song Title: </label>
                        <input type="text"
                            required
                            className="form-control"
                            value={this.state.songTitle}
                            onChange={this.onChangeSongTitle}
                        />
                    </div>
                    <div className="form-group">
                        <label>Artist: </label>
                        <input type="text"
                            required
                            className="form-control"
                            value={this.state.artist}
                            onChange={this.onChangeArtist}
                        />
                    </div>
                    <div className="form-group">
                        <input type="submit" value="search" className="btn btn-primary" />
                    </div>
                    {this.state.lyrics &&
                        <div className="form-group">
                            <StarIcon onClick={() => this.onAddToFavoritesClicked()}></StarIcon>
                        </div>
                    }
                </form>

                <div>
                    {this.state.lyrics &&
                        <FontSizeChanger
                            targets={['#target .content']}
                            onChange={(element, newValue, oldValue) => {
                                console.log(element, newValue, oldValue);
                            }}
                            options={{
                                stepSize: 2,
                                range: 3
                            }}
                            customButtons={{
                                up: <span style={{ 'fontSize': '36px' }}>+</span>,
                                down: <span style={{ 'fontSize': '20px' }}>-</span>,
                                style: {
                                    backgroundColor: 'red',
                                    color: 'white',
                                    WebkitBoxSizing: 'border-box',
                                    WebkitBorderRadius: '5px',
                                    width: '60px'
                                },
                                buttonsMargin: 10
                            }}
                        />
                    }
                    <div id="target">
                        <p className="content">{this.state.lyrics} </p>
                       
                    </div>
                </div>
            </div>
        )
    }
}
