import React, { useState } from "react";
import { BrowserRouter, Link, Route, Switch, Redirect } from "react-router-dom";
import axios from "axios";
import Home from "./Home";


function LogAndSign() {

    const [registerUsername, setRegisterUsername] = useState("");
    const [registerPassword, setRegisterPassword] = useState("");
    const [loginUsername, setLoginUsername] = useState("");
    const [loginPassword, setLoginPassword] = useState("");
    const [data, setData] = useState(null);

    const register = () => { //when it is called it sends the logic to backend
        axios({
            method: "POST",
            data: {
                username: registerUsername,
                password: registerPassword,
            },
            withCredentials: true,
            url: "http://localhost:4000/register",
        }).then((res) => {
            setData(res.data);
            console.log("res.data: ", res.data)
        });
    };
    const login = () => {
        axios({
            method: "POST",
            data: {
                username: loginUsername,
                password: loginPassword,
            },
            withCredentials: true,
            url: "http://localhost:4000/login",
        }).then((res) => {
            setData(res.data);
            console.log("client login res.data: ", res.data)
        });

        return <Redirect to="/Home/" />
    };


    return (
        <BrowserRouter>
            <div className="App">
                {/* <Home/> */}
                <div>
                    <h1>Register</h1>
                    <input
                        placeholder="username"
                        onChange={(e) => setRegisterUsername(e.target.value)}
                    />
                    <input
                        placeholder="password"
                        onChange={(e) => setRegisterPassword(e.target.value)}
                    />
                    <button onClick={register}>Submit</button>

                </div>

                <div>
                    <h1>Login</h1>
                    <input
                        placeholder="username"
                        onChange={(e) => setLoginUsername(e.target.value)}
                    />
                    <input
                        placeholder="password"
                        onChange={(e) => setLoginPassword(e.target.value)}
                    />
                    {/* <Link onClick={login} exact to="/Home">Submit</Link> */}
                    <button onClick={login}>Submit</button>
                </div>
            </div>

            {/* <Switch>
                <Route exact path="/Home" render={() => <Home user={data} />} />
            </Switch> */}

        </BrowserRouter>
    )
}

export default LogAndSign
