import React, { Component, useState } from 'react'
import { Tabs, Tab, AppBar } from "@material-ui/core";
import Lyrics from './Lyrics'
import FavoritesList from './FavoritesList'
import axios from 'axios';
import { getLyrics, getSong } from 'genius-lyrics-api';

export class Home extends Component {

    constructor(props) {
        super(props);

        this.handleChange = this.handleChange.bind(this);
        this.onFavoriteClicked = this.onFavoriteClicked.bind(this);
        this.onDeleteFavorite = this.onDeleteFavorite.bind(this);
        this.onAddToFavoritesClicked = this.onAddToFavoritesClicked.bind(this);
        this.onSearchClicked = this.onSearchClicked.bind(this);

        this.state = {
            selectedTab: props.selectedTab ? props.selectedTab : 0,
            lyrics: '',
            songToDisplay: '',
            user:props.user
        }
    }

    onAddToFavoritesClicked() {
         console.log("in home onAddToFavoritesClicked ");
        
        // const favoriteSong = {
        //     songTitle: this.state.songToDisplay.songTitle,
        //     artist: this.state.songToDisplay.artist,
        // }
        // console.log("favoriteSong: ",favoriteSong);
        // var newFavorites = this.props.user.favorites;
        // newFavorites.push(favoriteSong);
        // var updatdUser = {
        //     username: this.props.user.username,
        //     password: this.props.user.password,
        //     favorites: newFavorites,
        //     id: this.props.user._id
        // }

        // //add to favorites in db
        // axios.post('http://localhost:4000/update', updatdUser)
        //     .then(res => {
        //         console.log("lyrics update req: ", res.data)
        //     });
    }

    handleChange(event, newValue) {
        console.log("events,newvalue: ", event, newValue);
        this.setState({ selectedTab: newValue })
    };

    onDeleteFavorite(index) {
        console.log("onDeleteFavorite this.props.user.favorites: ", this.props.user.favorites);
        var newFavorites =  this.props.user.favorites;
        newFavorites.splice(index, 1);

        console.log("Lyrics newFavorites arr ", newFavorites);

        var updatdUser = {
            username: this.props.user.username,
            password: this.props.user.password,
            favorites: newFavorites,
            id: this.props.user._id
        }

        //add to favorites in db
        axios.post('http://localhost:4000/update', updatdUser)
            .then(res => {
                console.log("lyrics update req: ", res.data)
            });

        this.setState({ user: updatdUser });
    }

    onFavoriteClicked(index) {
        console.log("home onFavoriteClicked index: ", index);
        console.log("song: ", this.props.user.favorites[index]);
        const song = this.props.user.favorites[index];
        this.setState({songToDisplay:song});
        const options = {
            apiKey: 'DCCS9ls4wWU8rAylkXUtzBlRrCoA1dIlFF5yINiSp210dT7u-UoZmaOuQYPD-ZPt',
            title: song.songTitle,
            artist: song.artist,
            optimizeQuery: true
        };
        this.setState({ selectedTab: 0 });
        console.log("selectedtab: ", this.state.selectedTab);

        console.log("options: \n", options);

        getLyrics(options).then((lyrics) => {
            //console.log("lyrics: ", lyrics);
            this.setState({ lyrics: lyrics })
            console.log("this.state.lyrics: ",this.state.lyrics);
        });

    }

    onSearchClicked(songTitle, artist) {
        const songToDisplay = {songTitle:songTitle,artist:artist};
        console.log("songToDisplay: ",songToDisplay);
        this.setState({songToDisplay:songToDisplay});
        const options = {
            apiKey: 'DCCS9ls4wWU8rAylkXUtzBlRrCoA1dIlFF5yINiSp210dT7u-UoZmaOuQYPD-ZPt',
            title: songTitle,
            artist: artist,
            optimizeQuery: true
        };

        getLyrics(options).then((lyrics) => {
            console.log("lyrics: ", lyrics);
            this.setState({ lyrics: lyrics })
        });
    }

    onAddToFavoritesClicked() {
        console.log("song to display",this.state.songToDisplay);

        const favoriteSong = {
            songTitle: songTitle,
            artist: artist,
        }
        var newFavorites = this.props.user.favorites;
        console.log("favirites before: ",newFavorites);
        newFavorites.push(favoriteSong);
        var updatdUser = {
            username: this.props.user.username,
            password: this.props.user.password,
            favorites: newFavorites,
            id: this.props.user._id
        }

        console.log("favirites afteer: ",updatdUser.favorites);


        //add to favorites in db
        // axios.post('http://localhost:4000/update', updatdUser)
        //     .then(res => {
        //         console.log("lyrics update req: ", res.data)
        //     });
    }

    render() {
        return (
            <>
                <AppBar position="static">
                    <Tabs value={this.state.selectedTab} onChange={this.handleChange}>
                        <Tab label="Lyrics" />
                        <Tab label="Favorites" />
                    </Tabs>
                </AppBar>

                { this.state.selectedTab === 0 && <Lyrics lyrics={this.state.lyrics}
                    user={this.props.user}
                    song={this.state.songToDisplay}
                    onAddToFavoritesClicked={this.onAddToFavoritesClicked}
                    onSearchClicked={this.onSearchClicked}
                />}
                { this.state.selectedTab === 1 && <FavoritesList user={this.props.user}
                    onFavoriteClicked={this.onFavoriteClicked}
                    onDeleteFavorite={this.onDeleteFavorite}
                />}
            </>

        )
    }
}

export default Home
