import React, { Component } from 'react'
import List from '@material-ui/core/List';
import IconButton from '@material-ui/core/IconButton';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import DeleteIcon from '@material-ui/icons/Delete';
import ListItem from '@material-ui/core/ListItem';
import axios from 'axios';
import { getLyrics, getSong } from 'genius-lyrics-api';
import Lyrics from './Lyrics';
import Home from './Home';

export class FavoritesList extends Component {
    constructor(props) {
        super(props);
        console.log("in FavoritesList props.user: ", props.user);
        this.state = {
            favoritesList: this.props.user.favorites,
            lyrics:''
        }
    }

    onDeleteFavorite(index) {
        console.log("onDeleteFavorite this.props.user.favorites: ", this.state.favoritesList);
        var newFavorites = this.state.favoritesList;
        newFavorites.splice(index, 1);

        console.log("Lyrics newFavorites arr ", newFavorites);

        var updatdUser = {
            username: this.props.user.username,
            password: this.props.user.password,
            favorites: newFavorites,
            id: this.props.user._id
        }

        //add to favorites in db
        axios.post('http://localhost:4000/update', updatdUser)
            .then(res => {
                console.log("lyrics update req: ", res.data)
            });

        this.setState({ favoritesList: newFavorites });
    }

    onItemClicked(index) {
        console.log("song: ",this.state.favoritesList[index]);
        const song = this.state.favoritesList[index];
        const options = {
            apiKey: 'DCCS9ls4wWU8rAylkXUtzBlRrCoA1dIlFF5yINiSp210dT7u-UoZmaOuQYPD-ZPt',
            title: song.songTitle,
            artist: song.artist,
            optimizeQuery: true
        };

        console.log("options: \n", options);

        getLyrics(options).then((lyrics) => {
            console.log("lyrics: ", lyrics);
            this.setState({ lyrics: lyrics })
        });

        return (
            <Home selectedTab={0}/>
        )
    }

    render() {
        return (
            <div>
                <List >
                    {
                        (this.props.user.favorites) && (
                            this.props.user.favorites.map((item, index) => {
                                return (
                                    <ListItem key={index} button={true} onClick={()=>this.onItemClicked(index)}>
                                        <ListItemText key={index}
                                            primary={item.artist + '-' + item.songTitle}
                                        />
                                        <ListItemSecondaryAction>
                                            <IconButton onClick={() => this.onDeleteFavorite(index)} edge="end" aria-label="delete">
                                                <DeleteIcon />
                                            </IconButton>
                                        </ListItemSecondaryAction>
                                    </ListItem>
                                )
                            })
                        )
                    }

                </List>
            </div>
        )
    }
}

export default FavoritesList
